  PAYER_NAME, PAYEE_ACCOUNT, PAYEE_NAME, 
  BANK_CODE, TRADE_AMOUNT, TRADE_STATUS, 
  TRADE_STATUS_DESC, EXCEPTION_REASON, EXCEPTION_TYPE ,CREATE_TIME
  )
values (#{id,jdbcType=INTEGER}, #{channelId,jdbcType=INTEGER}, #{reconId,jdbcType=INTEGER}, #{orderNo,jdbcType=VARCHAR}, #{payerAccount,jdbcType=VARCHAR}, 
  #{payerName,jdbcType=VARCHAR}, #{payeeAccount,jdbcType=VARCHAR}, #{payeeName,jdbcType=VARCHAR}, 
  #{bankCode,jdbcType=VARCHAR}, #{tradeAmount,jdbcType=DECIMAL}, #{tradeStatus,jdbcType=VARCHAR}, 
  #{tradeStatusDesc,jdbcType=VARCHAR}, #{exceptionReason,jdbcType=VARCHAR}, #{exceptionType,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select  EXCEPTION_TYPE ,
count(*) EXCEPTION_COUNT
from t_recon_exception_trade 
where CHANNEL_ID = #{channelId,jdbcType=INTEGER} 
AND RECON_ID = #{reconId,jdbcType=INTEGER}
group by EXCEPTION_TYPE

select t.ORDER_NO, t.CHANNEL_ID, t.RECON_ID, t.PAYER_ACCOUNT, t.PAYER_NAME, t.PAYEE_ACCOUNT, t.PAYEE_NAME, t.TRADE_AMOUNT, t.TRADE_STATUS, t.EXCEPTION_REASON, t.EXCEPTION_TYPE from t_recon_exception_trade t where t.CHANNEL_ID = #{channelId,jdbcType=INTEGER} AND t.RECON_ID = #{reconId,jdbcType=INTEGER}
AND t.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
AND t.EXCEPTION_TYPE = #{exceptionType,jdbcType=INTEGER}

select 

from t_recon_exception_trade t
where t.EXCEPTION_TYPE = #{exceptionType,jdbcType=INTEGER}
