mapper.select:
	 	SELECT 
			l.id, l.type, l.merchant_id, l.period, l.amount_limit, l.modifer, l.modify_time
		FROM 
			t_amount_limit l, t_merchant_base_info m
		where l.merchant_id = m.id

        and l.type = #{type,jdbcType=INTEGER}


       and m.name like concat ('%',#{orgName,jdbcType=VARCHAR},'%')


       and m.name like concat ('%',#{merchantName,jdbcType=VARCHAR},'%')


       and l.modify_time > #{startTime}


       and l.modify_time < #{endTime}
