mapper.select:
SELECT t.id, t.batch_no, t.trade_time, t.total_amount, t.total_count, t.payer_merchant_id, m.type, m. NAME, t. STATUS, t.creator, t.create_time, t.auditor, t.audit_time FROM t_batch_trade t, t_merchant_base_info m WHERE t.payer_merchant_id = m.id
and t.status = #{status,jdbcType=INTEGER}
and t.trade_time > #{startTime}
and t.trade_time < #{endTime}

mapper.update:
update t_batch_trade
set status = #{status,jdbcType=INTEGER},
  audit_optinion = #{auditOptinion,jdbcType=VARCHAR},
  auditor = #{auditor,jdbcType=VARCHAR},
  audit_time = #{auditTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
