mapper.select:
SELECT mr.id, mr.type, mr.channel_id, ch.channel_name, mr.merchant_id, mb. NAME merchant_name, mr.loaning, mr.loaning_org_id, org. NAME org_name, mr.loaning_amount, mr.STATUS, mr.modifer, mr.modify_time FROM t_merchant_route_conf mr LEFT JOIN t_merchant_base_info mb ON mr.merchant_id = mb.id LEFT JOIN t_channel ch ON mr.channel_id = ch.id LEFT JOIN t_merchant_base_info org ON mr.loaning_org_id = org.id where 1=1
and mr.type = #{type,jdbcType=INTEGER}
and mr.status = #{status,jdbcType=INTEGER}
and mb.name like concat('%',#{merchantName,jdbcType=VARCHAR},'%')
and mr.modify_time > #{startTime}
and mr.modify_time < #{endTime}
and (mr.merchant_id = #{userMerchantId,jdbcType=INTEGER} or mb.org_id = #{userMerchantId,jdbcType=INTEGER})
and mr.merchant_id = #{userMerchantId,jdbcType=INTEGER}

select 

from t_merchant_route_conf
where channel_id = #{channelId,jdbcType=INTEGER}
  and merchant_id = #{merchantId,jdbcType=INTEGER}
