mapper.update:
update t_trade
set trade_time = #{tradeTime,jdbcType=TIMESTAMP},
	status = #{status,jdbcType=INTEGER}
where batch_no = #{batchNo,jdbcType=VARCHAR}

mapper.select:
select orderNo
from t_trade
where order_no = #{orderNo,jdbcType=VARCHAR}
	and merchant_id = #{merchantId,jdbcType=INTEGER}

select count(1) total_count, ifnull(sum(trade_amount),0) total_amount
from t_trade
where 1 = 1
  
    and merchant_id = #{merchantId,jdbcType=INTEGER}
  
  
   and trade_time > #{startTime}
  
  
   and trade_time < #{endTime}

select count(1) total_count, ifnull(sum(trade_amount),0) total_amount
from t_trade t,t_merchant_route_conf c
where t.merchant_id = c.merchant_id
    and t.channel_id = c.channel_id
    and c.loaning = 1
    and t.status = 2
  
    and t.merchant_id = #{merchantId,jdbcType=INTEGER}
  
  
    and t.merchant_id = #{orgId,jdbcType=INTEGER}
  
  
   and t.trade_time > #{startTime}
  
  
   and t.trade_time < #{endTime}

SELECT t.trade_time, o.`name` org_name, t.batch_no, t.order_no, m.`code` merchant_code, m.`name` merchant_name, t.`status`, t.trade_amount, c.loaning FROM t_trade t LEFT JOIN t_merchant_base_info m ON t.merchant_id = m.id LEFT JOIN t_merchant_base_info o ON m.org_id = o.id LEFT JOIN t_merchant_route_conf c ON t.merchant_id = c.merchant_id AND t.channel_id = c.channel_id where 1 = 1
and t.merchant_id = #{merchantId,jdbcType=INTEGER}
and (o.id = #{orgId,jdbcType=INTEGER} or t.merchant_id = #{orgId,jdbcType=INTEGER})
and c.loaning = #{loaning,jdbcType=INTEGER}
and t.order_no = #{orderNo,jdbcType=VARCHAR}
and t.batch_no = #{batchNo,jdbcType=VARCHAR}
and t.trade_time > #{startTime}
and t.trade_time < #{endTime}

SELECT stat_date, merchant_code, merchant_name, org_name, count(1) total_count, sum(trade_amount) total_amount, SUM(CASE WHEN loaning=1 THEN trade_amount ELSE 0 END) loaning_total_amount, sum(fee) feeAmount from (SELECT DATE_FORMAT(t.trade_time,'%Y-%m-%d') stat_date, t.trade_amount, t.loaning, m.code merchant_code, m.name merchant_name, b.`name` org_name, f.pay_fee, f.loan_fee, t.merchant_fee_amount fee FROM t_trade t, t_merchant_fee_conf f, t_merchant_base_info m, t_merchant_base_info b WHERE t.merchant_id = f.merchant_id and f.merchant_id = m.id and m.type in (4,5) and m.org_id = b.id AND t.`status` = 2
and t.merchant_id = #{merchantId,jdbcType=INTEGER}
and b.id = #{orgId,jdbcType=INTEGER}
and t.trade_time > #{startTime}
and t.trade_time < #{endTime}
)temp group by stat_date,merchant_code,merchant_name,org_name

SELECT stat_date, merchant_code, merchant_name, count(1)total_count, sum(trade_amount)total_amount, SUM(CASE WHEN loaning=1 THEN trade_amount ELSE 0 END) loaning_total_amount, -sum(fee) feeAmount from (SELECT DATE_FORMAT(t.trade_time,'%Y-%m-%d') stat_date, t.trade_amount, t.loaning, m.code merchant_code, m.name merchant_name, f.pay_fee, f.loan_fee, t.merchant_fee_amount fee FROM t_trade t, t_merchant_fee_conf f, t_merchant_base_info m WHERE t.merchant_id = f.merchant_id and f.merchant_id = m.id AND t.`status` = 2 and m.type in (1,2,3)
and t.merchant_id = #{orgId,jdbcType=INTEGER}
and t.trade_time > #{startTime}
and t.trade_time < #{endTime}
)temp group by stat_date,merchant_code,merchant_name

SELECT stat_date, merchant_code, merchant_name, count(1)total_count, sum(trade_amount)total_amount, SUM(CASE WHEN loaning=1 THEN trade_amount ELSE 0 END) loaning_total_amount, sum(fee) feeAmount from (SELECT DATE_FORMAT(t.trade_time,'%Y-%m-%d') stat_date, t.trade_amount, t.loaning, b.name merchant_name, b.`code` merchant_code, f.pay_fee, f.loan_fee, t.org_benefit fee FROM t_trade t, t_merchant_fee_conf f, t_merchant_fee_conf of, t_merchant_base_info m, t_merchant_base_info b WHERE t.merchant_id = f.merchant_id and f.merchant_id = m.id and m.org_id = b.id and m.org_id = of.merchant_id AND t.`status` = 2
and b.id = #{orgId,jdbcType=INTEGER}
and t.trade_time > #{startTime}
and t.trade_time < #{endTime}
)temp group by stat_date,merchant_code,merchant_name
